#!/bin/bash
# use SDL 2.0.6 because commit e8c1446 in 2.0.7 broke mac audio, at least for Boxedwine
gcc -std=c++11 -Wall -Wno-unused-private-field -Wno-unused-variable -Wno-parentheses-equality -Wno-unused-function -I../../include ../../source/sdl/*.cpp ../../source/sdl/singleThreaded/*.cpp ../../platform/linux/*.cpp ../../source/emulation/cpu/*.cpp ../../source/emulation/cpu/common/*.cpp ../../source/emulation/cpu/normal/*.cpp ../../source/emulation/softmmu/*.cpp ../../source/io/*.cpp ../../source/kernel/*.cpp ../../source/kernel/devs/*.cpp ../../source/kernel/proc/*.cpp ../../source/kernel/loader/*.cpp ../../source/util/*.cpp ../../source/opengl/sdl/*.cpp ../../source/opengl/*.cpp -o boxedwine -lm -lz -lc++ -lminizip -DBOXEDWINE_RECORDER -DBOXEDWINE_HAS_SETJMP -DBOXEDWINE_64 -DBOXEDWINE_ZLIB -DSDL2=1 "-DGLH=<SDL_opengl.h>" -DBOXEDWINE_OPENGL_SDL_MAC -I../../lib/sdlMac2.0.6/SDL2.framework/Headers -D_THREAD_SAFE -L../../lib/sdlMac2.0.6/SDL2.framework -lSDL2 -L../../lib/minizipMac/Minizip.framework -O2 -framework OpenGL
